double findMedianSortedArrays(int* nums1, int nums1Size, int* nums2, int nums2Size){    if (nums1Size > nums2Size) {        int tmp = nums1Size;        nums1Size = nums2Size;        nums2Size = tmp;        int *ptrTmp = nums1;        nums1 = nums2;        nums2 = ptrTmp;    }        int left = 0, right = nums1Size;    while (left <= right) {        int len1 = left + ((right - left) >> 1);        int len2 = (nums1Size + nums2Size) / 2 - len1;        if ((len1 - 1 > -1) && (len1 - 1 < nums1Size) && (len2 > -1) && (len2 < nums2Size) && (nums1[len1 - 1] > nums2[len2])) {            right = len1 - 1;        } else if ((len2 - 1 > -1) && (len2 - 1 < nums2Size) && (len1 > -1) && (len1 < nums1Size) && (nums1[len1] < nums2[len2 - 1])) {            left = len1 + 1;        } else {            int num1Max = len1 > 0 ? nums1[len1 - 1] : INT_MIN;            int num2Max = len2 > 0 ? nums2[len2 - 1] : INT_MIN;            int numMax = num1Max > num2Max ? num1Max : num2Max;            int num1Min = len1 < nums1Size ? nums1[len1] : INT_MAX;            int num2Min = len2 < nums2Size ? nums2[len2] : INT_MAX;            int numMin = num1Min < num2Min ? num1Min : num2Min;            if ((nums1Size + nums2Size) % 2) {                return numMin;            } else {                                return (numMax + (num1Min < num2Min ? num1Min : num2Min)) / 2.0;            }        }    }    return 0.0;}